// App.js - Nova Chat Application
import React, { useState } from 'react';
import { theme } from './theme';
import Sidebar from './components/Sidebar';
import ChatMessage from './components/ChatMessage';
import ChatInput from './components/ChatInput';

function App() {
  const [activeTab, setActiveTab] = useState('chat');
  const [selectedMode, setSelectedMode] = useState('Practical tools');
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "I've been feeling really overwhelmed with work lately, and I'm not sure how to handle it all",
      isUser: true,
    },
    {
      id: 2,
      text: "I see you're experiencing a sense of overwhelm with your work. Tell me more about what's been causing you to feel this way.",
      isUser: false,
      showFeedback: true,
    },
  ]);
  const [isThinking, setIsThinking] = useState(false);

  const handleSend = (message, mode) => {
    const newMessage = {
      id: messages.length + 1,
      text: message,
      isUser: true,
    };
    setMessages([...messages, newMessage]);

    // Show thinking indicator and delay AI response
    setIsThinking(true);

    setTimeout(() => {
      const aiResponse = {
        id: newMessage.id + 1,
        text: `Thank you for sharing. I'm here to listen and support you through this.`,
        isUser: false,
        showFeedback: true,
      };
      setMessages((prev) => [...prev, aiResponse]);
      setIsThinking(false);
    }, 1500);
  };

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: theme.colors.surface.raised }}>
      {/* Sidebar */}
      <Sidebar activeTab={activeTab} onTabChange={setActiveTab} userName="Sarah" />

      {/* Main Chat Area */}
      <div
        style={{
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          height: '100vh',
          overflow: 'hidden',
        }}
      >
        {/* Messages Container */}
        <div
          style={{
            flex: 1,
            overflowY: 'auto',
            overflowX: 'hidden', // Prevent horizontal overflow
            padding: '48px 24px 0 24px', // ADD: 48px top padding, 0px bottom
            display: 'flex',
            flexDirection: 'column',
            gap: `${theme.spacing[8]}px`,
            maxWidth: '800px',
            margin: '0 auto',
            width: '100%',
            boxSizing: 'border-box',
          }}
        >
          {messages.map((msg) => (
            <ChatMessage
              key={msg.id}
              message={msg.text}
              isUser={msg.isUser}
              showFeedback={msg.showFeedback}
            />
          ))}
          {isThinking && (
            <ChatMessage
              key="thinking"
              message="..."
              isUser={false}
              showFeedback={false}
              avatarPulse={true}
            />
          )}
        </div>

        {/* Input Area */}
        <div
          style={{
            padding: '16px 24px', // Reduce paddings (was theme.spacing[8]px)
            maxWidth: '800px',
            margin: '0 auto',
            width: '100%',
            boxSizing: 'border-box',
          }}
        >
          <ChatInput
            onSend={handleSend}
            selectedMode={selectedMode}
            onModeChange={setSelectedMode}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
