// theme.js - Your Design System
export const theme = {
    colors: {
      // Primary
      primary: '#0D0D0C',
      primaryHover: '#262524',
      primarySubtle: '#F2EDE3',

      // Accent (blue)
      accent: '#1D60C7',
      accentHover: '#001636',
      accentSubtle: '#D4E0F3',

      // Neutrals (warm grays)
      neutral: {
        50: '#FFFFFF',
        100: '#F2EDE3',
        200: '#E6E0D7',
        300: '#CCC7BF',
        400: '#B3AEA7',
        500: '#8C8983',
        600: '#666460',
        700: '#4D4B48',
        800: '#262524',
        900: '#0D0D0C',
      },

      // Text colors
      text: {
        primary: '#0D0D0C',
        secondary: '#666460',
        inverse: '#FFFFFF',
        disabled: '#B3AEA7',
        link: '#1D60C7',
      },

      // Surface colors
      surface: {
        default: '#FFFFFF',
        raised: '#F2EDE3',
        overlay: '#FFFFFF',
      },

      // Border colors
      border: {
        default: '#CCC7BF',
        subtle: '#E6E0D7',
        focus: '#1D60C7',
      },

      // Feedback colors
      feedback: {
        success: '#31B62F',
        successSubtle: '#D0F2CF',
        warning: '#EA9F0E',
        warningSubtle: '#F8E5C0',
        error: '#BD0E0E',
        errorSubtle: '#FFD9D9',
        info: '#1D60C7',
        infoSubtle: '#D4E0F3',
      },
    },

    spacing: {
      0: 0,
      1: 4,
      2: 8,
      3: 12,
      4: 16,
      5: 20,
      6: 24,
      8: 32,
      10: 40,
      12: 48,
      16: 64,
      20: 80,
      24: 96,
    },

    radius: {
      none: 0,
      sm: 6,
      md: 12,
      lg: 20,
      full: 1000,
    },

    typography: {
      fontFamily: '"Inter Display", -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif',
      fontFeatures: "'ss07' on, 'ss08' on, 'cv05' on",
      letterSpacing: '0.01rem',

      weights: {
        regular: 400,
        medium: 500,
        semibold: 600,
        bold: 700,
      },

      sizes: {
        xs: '12px',
        sm: '14px',
        md: '16px',
        lg: '18px',
        xl: '20px',
        '2xl': '24px',
        '3xl': '32px',
      },

      lineHeights: {
        tight: 1.2,
        snug: 1.3,
        normal: 1.5,
        relaxed: 1.6,
      },
    },
  };
